#!/usr/bin/perl

$os = $^O;

if ($os =~ /MSWin32/) {
  $diff = "..\\diff_r3\\win32\\diff.exe";
  $bi   = "..\\bi\\win32\\bi.exe";
  $si   = "..\\bi\\win32\\si.exe";
}
elsif ($os =~ /linux/) {
	$diff = "../diff_r3/linux/diff.exe";
	$bi   = "../bi/linux/bi.exe";
	$si   = "../bi/linux/si.exe";
}

sub excmd() {
	my ($cmd) = @_;
	print "$cmd\n";
	$info = `$cmd`;
}

sub getpsi() {
  my ($si1, $si2) = @_;
  
  # 1. PSI for native
	## we assume the existence of bi1.txt generated by r3.pl
	## rold:  how many references in the old program?
	## rnew:  how many references in the new program?
	## match: how many references in the new program match that in the old program?
	my @oldsymname=();
	my @oldsymaddr=();
	my @newsymname=();
	my @newsymaddr=();
		
	open si, "<$si1" or die "cannot open $si1\n";
	while (<si>) {
		chomp;
		if (/^ref_sym/) {
			@rs = split;
			push @oldsymname, $rs[1];
			push @oldsymaddr, hex("$rs[2]");
		}
	}
	close si;
	open si, "<$si2" or die "cannot open $si2\n";
	while (<si>) {
		chomp;
		if (/^ref_sym/) {
			@rs = split;
			push @newsymname, $rs[1];
			push @newsymaddr, hex("$rs[2]");
		}
	}
	close si;
	## how many matched?
	$match=0;
	for ($i=0; $i<@newsymname; $i++) {
		for ($j=0; $j<@oldsymname; $j++) {
			if ( ($newsymname[$i] eq $oldsymname[$j]) &&
			     ($newsymaddr[$i] == $oldsymaddr[$j]) ) 
			{
			  $match++;
			  last;	
			}
		}
	}
	$oldcnt = @oldsymname;
	$newcnt = @newsymname;
	
	return $match / ($oldcnt>$newcnt ? $oldcnt : $newcnt);
}

open cc, "<changecases.lst" or die "cannot open changecases.lst\n";
while (<cc>) {
	chomp;
	
  if ($_) {
  	@rs = split;
  	$no = $rs[0];
  	$dir1 = "../benchmarks/$rs[1]";
		$dir2 = "../benchmarks/$rs[2]";
        $bmk1 = $rs[1]; $bmk2 = $rs[2];
		
		print ">>>processing case\#$no: $dir1 -> $dir2\n";
		
		# diff the two raws
		&excmd("perl ./hex2raw.pl $dir1/build/telosb/main-n.ihex $dir1/build/telosb/main-n.raw > $dir1/build/telosb/hex2raw-n.log");
		&excmd("perl ./hex2raw.pl $dir2/build/telosb/main-n.ihex $dir2/build/telosb/main-n.raw > $dir2/build/telosb/hex2raw-n.log");
		
		# old file, new file, delta file
		&excmd("$diff $dir1/build/telosb/main-n.raw $dir2/build/telosb/main-n.raw ../benchmarks/delta-$no.raw > ../benchmarks/native-$no.log");
		$deltasize = -s "../benchmarks/delta-$no.raw";
		
		# psi??
		&excmd("$si $dir1/build/telosb/main-r2.exe >$dir1/build/telosb/si-n.txt");
		&excmd("$si $dir2/build/telosb/main-r2.exe >$dir2/build/telosb/si-n.txt");
		$psi = &getpsi("$dir1/build/telosb/si-n.txt", "$dir2/build/telosb/si-n.txt");
		
		print "<<< $bmk1 $bmk2 $deltasize $psi\n";
	}
}
close cc;

exit;

